import Head from 'next/head';
import Container from '../components/container';

function Index({ gifs: { data } }) {
  return (
    <Container>
      <Head></Head>
      <div className="flex flex-wrap inline">
        {data.map((gif) => (
          <li className="my-2 mr-2 w-64 h-auto" key={gif.id}>
            <img
              className="rounded-lg"
              src={gif.images.downsized_medium.url}
            ></img>
          </li>
        ))}
        <style jsx>{`
          li {
            list-style-type: none;
          }
        `}</style>
      </div>
    </Container>
  );
}

export async function getStaticProps() {
  const q = 'darth vader';
  const apiKey = 'MABWKnnDnZ9l8dALV6HfxnH1DVYpoX1J';
  const response = await fetch(
    `http://api.giphy.com/v1/gifs/search?q=${q}&api_key=${apiKey}&limit=12`
  );
  const gifs = await response.json();

  return {
    props: {
      gifs,
    },
  };
}

export default Index;
