import Container from '../components/container';

function Index({ gifs: { data } }) {
  return (
    <Container>
      <div className="flex flex-wrap">
        {data.map((gif) => (
          <li
            className="my-2 mr-2 w-64 h-auto list-none cursor-pointer"
            key={gif.id}
          >
            <img
              className="rounded-lg transition duration-500 ease-in-out transform hover:-translate-y-1 hover:scale-105"
              src={gif.images.downsized_medium.url}
            ></img>
          </li>
        ))}
      </div>
    </Container>
  );
}

export async function getStaticProps() {
  const q = 'mario odyssey';
  const apiKey = 'MABWKnnDnZ9l8dALV6HfxnH1DVYpoX1J';
  const response = await fetch(
    `http://api.giphy.com/v1/gifs/search?q=${q}&api_key=${apiKey}&limit=12`
  );
  const gifs = await response.json();

  return {
    props: {
      gifs,
    },
  };
}

export default Index;
