import Head from 'next/head';
import Container from '../components/container';
import { useEffect, useState } from 'react';

function Index({ gifs: { data } }) {
  const [term, setTerm] = useState('');

  useEffect(async () => {
    const apiKey = 'MABWKnnDnZ9l8dALV6HfxnH1DVYpoX1J';
    const response = await fetch(
      `http://api.giphy.com/v1/gifs/search?q=${term}&api_key=${apiKey}&limit=12`
    );
    const data = await response.json();
    return data;
  }, []);

  return (
    <Container>
      <Head>
        <link
          href="https://fonts.googleapis.com/icon?family=Material+Icons"
          rel="stylesheet"
        />
      </Head>
      <form>
        <input
          className="shadow appearance-none border rounded w-full py-2 my-6 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          id="search"
          type="text"
          placeholder="BÃºsqueda"
          autoComplete="off"
          onChange={(e) => setTerm(e.target.value)}
        />
      </form>
      <div className="flex flex-wrap">
        {data.map((gif) => (
          <li className="my-2 mr-2 w-64 h-auto" key={gif.id}>
            <img
              className="rounded-lg"
              src={gif.images.downsized_medium.url}
            ></img>
          </li>
        ))}
        <style jsx>{`
          li {
            list-style-type: none;
          }
        `}</style>
      </div>
    </Container>
  );
}

export async function getStaticProps() {
  const apiKey = 'MABWKnnDnZ9l8dALV6HfxnH1DVYpoX1J';
  const response = await fetch(
    `http://api.giphy.com/v1/gifs/search?q=${term}&api_key=${apiKey}&limit=12`
  );
  const gifs = await response.json();

  return {
    props: {
      gifs,
    },
  };
}

export default Index;
